import React, { Component } from "react";
import { connect } from  'react-redux';

import CounterControl from "../../components/CounterControl/CounterControl";
import CounterOutput from "../../components/CounterOutput/CounterOutput";
import { increment } from "../../store/actions/actions";
class Counter extends Component {


  render() {
    return (
      <div>
        <CounterOutput value={this.props.ctr} />
        <CounterControl
          label="Increment"
          clicked={this.props.onIncrementCounter}
        />
        <CounterControl
          label="Decrement"
          clicked={this.props.onDecrementCounter}
        />
        <CounterControl
          label="Add 5"
          clicked={this.props.onAdd5Counter}
        />
        <CounterControl
          label="Subtract 5"
          clicked={this.props.onSubtract5Counter}
        />
        <button onClick={()=>this.props.onStoreResult(this.props.ctr)}>Store Results</button>
        <ul>
          {this.props.storedResults.map(strResult => {
              return(<li key={strResult.id} onClick={() => this.props.onDeleteResult(strResult.id)}>{strResult.value}</li>)
          })}
        </ul>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    ctr:state.ctr.counter,
    storedResults:state.res.results
  };
}

const mapDispatchToProps = dispatch => {
  return {
    onIncrementCounter : () => dispatch(increment()),
    onDecrementCounter : () => dispatch(decrement()),
    onAdd5Counter : () => dispatch(add5(5)),
    onSubtract5Counter : () => dispatch(5),
    onStoreResult: (result) => dispatch(result),
    onDeleteResult: (id) => dispatch(id)
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Counter);
