import * as type from '../actions/actionsTypes';


const initialState = {
    counter:0
}


const counterReducer = (state = initialState, action) => {

    switch(type.type){
        case type.INCREMENT:
            return {
                ...state,
                counter: state.counter+1
            };
        case type.DECREMENT:
            return {
                ...state,
                counter: state.counter-1
            }
        case type.ADD5:
            return {
                ...state,
                counter: state.counter+action.val
            };
        case type.SUBTRACT5:
            return {
                ...state,
                counter: state.counter-action.val
            };
        default:
            return state

    }
}

export default counterReducer;
