const initialState = {
    counter:0,
    results:[]
}


const reducer = (state = initialState, action) => {

    switch(action.type){
        case "INCREMENT":
            return {
                ...state,
                counter: state.counter+1
            };
        case "DECREMENT":
            return {
                ...state,
                counter: state.counter-1
            }
        case "ADD5":
            return {
                ...state,
                counter: state.counter+action.val
            };
        case "SUBTRACT5":
            return {
                ...state,
                counter: state.counter-action.val
            };
        case "ADD_RESULT":
            return{
                ...state,
                results: state.results.concat(state.counter)
            }

    }
    return state
}

export default reducer;
