import React, { Component } from 'react';
import axios from 'axios';
import Course from '../course/course';
import './courses.css';


let course;
class Courses extends Component {
    state ={
        courses:[],
        course:null
    }
    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/posts').then (
            response => {
            let data = response.data.slice(0, 4);
            this.setState({courses:data});
            }
        );
         this.setState({course : this.state.courses.map(course => {
            return (
                <h1 className="courses" onClick={this.courseHandler}>{course.title}</h1>)
        })})
        //this.setState({cours:courses});
    }


    courseHandler=() =>{
        course = this.state.courses.map(course => {
            return (
                <Course 
                key={course.id}
                title={course.title}
                body ={course.body}
                id={course.id} />
            )
        });
    }
    render() {
        console.log(this.state.course);
        return(
            <div>
                {this.state.course}
            </div>
        );
    }

}

export default Courses;
