import React, { Component } from "react";
import { connect } from  'react-redux';

import CounterControl from "../../components/CounterControl/CounterControl";
import CounterOutput from "../../components/CounterOutput/CounterOutput";

class Counter extends Component {


  render() {
    return (
      <div>
        <CounterOutput value={this.props.ctr} />
        <CounterControl
          label="Increment"
          clicked={this.props.onIncrementCounter}
        />
        <CounterControl
          label="Decrement"
          clicked={this.props.onDecrementCounter}
        />
        <CounterControl
          label="Add 5"
          clicked={this.props.onAdd5Counter}
        />
        <CounterControl
          label="Subtract 5"
          clicked={this.props.onSubtract5Counter}
        />
        <button onClick={this.props.addResults}>Store Results</button>
        <ul>
          {this.props.rslt.map(results => {
              return(<li key={Math.random()}>{results}</li>)
          })}
        </ul>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    ctr:state.counter,
    rslt:state.results
  };
}

const mapDispatchToProps = dispatch => {
  return {
    onIncrementCounter : () => dispatch({type:"INCREMENT"}),
    onDecrementCounter : () => dispatch({type:"DECREMENT"}),
    onAdd5Counter : () => dispatch({type:"ADD5", val: 5}),
    onSubtract5Counter : () => dispatch({type:"SUBTRACT5", val: 5}),
    addResults: () => dispatch({type:"ADD_RESULT"})
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Counter);
